import React, { useState } from 'react';
import styled from 'styled-components';
import CommonEmailInput from '../Common/CommonEmailInput';
import FaaData from './FaqData';

const Faq = () => {

    const [seePara, setSeePara] = useState(false)

    function paraopen() {
        let p = document.getElementById("p");
        p.classList.toggle("pactive")
    }

    return (
        <>
            <FaqContainer>
                <div className="container-fluid p-5">
                    <div className="row">
                        <h1 className="text-center">Frequently Asked Questions</h1>
                        {
                            FaqData
                        }

                        <div className="email">
                            <CommonEmailInput />
                        </div>
                    </div>
                </div>
            </FaqContainer>
        </>
    )
}

const FaqContainer = styled.div`
            border-top:10px solid gray;
            .acc{
                    border-bottom:1px solid black;
                margin:1px auto;
                background:gray;
            width:50%;
            margin:10px auto;
            cursor:pointer;
            .acc-body{
                .acc-title{
                    display:flex;
            width:100%;
            justify-content:space-between;
            align-items:center;
                }
            margin: 2px auto;

    }
        .p, .times{
            display:none;
        }
        .pactive, .fa-active{
            display:flex;
        }
    }
            `

export default Faq
