import React, { useState } from 'react';
import styled from 'styled-components';
import FaqData from './FaqData';
import CommonEmailInput from '../Common/CommonEmailInput';

const Faq = () => {

    const [clicked, setClicked] = useState(false);

    const toggle = id => {
        if (clicked === id) {
            return setClicked(null)
        }
        setClicked(id)
    }

    return (
        <>
            <FaqContainer>
                <div className="container-fluid p-5">
                    <div className="row">
                        <h1 className="text-center">Frequently Asked Questions</h1>

                        {
                            FaqData.map(({ id, title, content }) => {
                                return (
                                    <div className="accordion">
                                        <div className="acc" onClick={() => (toggle(id))} key={id}>
                                            <h4>{title}</h4>
                                            <span>{clicked === id ? <i className="fas fa-times" /> :
                                                <i className="fas fa-plus" />}</span>
                                        </div>
                                        <div className="acc-ans">
                                            {
                                                clicked === id ?  <p>{content}</p> : null
                                            }
                                        </div>
                                    </div>
                                )
                            })
                        }

                        <div className="email">
                            <CommonEmailInput />
                        </div>
                    </div>
                </div>
            </FaqContainer>
        </>
    )
}

const FaqContainer = styled.div`
    border-top:10px solid gray;
    .accordion{
        background:#303030;
        width:60%;
        cursor:pointer;
        padding: 10px 20px;
        margin: 2px auto;
        .acc{
            display:flex;
            align-items:center;
            justify-content:space-between;
            h4{
                font-weight:400;
            }
        }
        .acc-ans{
            padding: 0px 0px;
            font-size: 1.2rem;
            font-weight:400;
            p{
                width:100%;
                border-top: 2px solid black;
            }
        }
    }
        @media (min-width:200px) and (max-width:600px){
            .accordion{
                width:100% !important;
            }
        }
`

export default Faq
