import React, { useState } from 'react';
import styled from 'styled-components';
import CommonEmailInput from '../Common/CommonEmailInput';

const Faq = () => {

    const [seePara, setSeePara] = useState(false)

    return (
        <>
            <FaqContainer>
                <div className="container-fluid p-5">
                    <div className="row">
                        <h1 className="text-center">Frequently Asked Questions</h1>
                        <div className="acc">
                            <div className="acc-body text-center d-flex">
                                <div className="acc-title" onClick={() => {
                                    setSeePara(true)
                                    console.log("hello")
                                }}>
                                    <h3>What is Netfliex</h3><i className="fas fa-plus"></i>
                                </div>
                            </div>
                            <div show={seePara}>
                                <p>
                                    Netflix is a streaming service that offers a wide variety of award-winning TV
                                    shows, movies, anime, documentaries and more – on thousands of internet-connected
                                    devices. You can watch as much as you want, whenever you want, without a single
                                    ad – all for one low monthly price. There's always something new to discover,
                                    and new TV shows and movies are added every week!
                                </p>
                            </div>
                        </div>

                        <div className="email">
                            <CommonEmailInput />
                        </div>
                    </div>
                </div>
            </FaqContainer>
        </>
    )
}

const FaqContainer = styled.div`
            border-top:10px solid gray;
            .acc{
                    border-bottom:1px solid black;
                margin:1px auto;
                background:red;
            width:50%;
            margin:10px auto;
            .acc-body{
                .acc-title{
                    display:flex;
            width:100%;
            justify-content:space-between;
            align-items:center;
                }
            margin: 2px auto;

            transform: ${props => props.show ? 'translateY(-1000%)' : 'translateY(0%)'}
    }
    }
            `

export default Faq
