import React from 'react';
import styled from 'styled-components';

const CommonEmailInput = () => {
    return (
        <>
         <CommonContainer>
             <div className="container">
                <div className="row d-flex justify-content-center">
                    <h3 className="pb-2">Ready to watch? Enter your email to create or restart your membership.</h3>
                    <form onSubmit={(e)=>{e.preventDefault();}}>
                    <input type="text" placeholder="Email address" required/><button>Get Started </button>
                    </form>
                </div>   
             </div>
         </CommonContainer>   
        </>
    )
}

const CommonContainer = styled.div`
    h3{
        font-size:1.2rem;
        font-weight:400;
        max-width:640px;
    }
    input{
        width: 45%;
        border:none;
    }
    button{
        width:30%;
        background:#e50914;
        color:white;
        font-size:1.1rem;
        border:none;
    }
    @media(max-width:992px){
        input,button{
            width: auto;
        }
    }
`

export default CommonEmailInput;
