import React from 'react';
import styled from 'styled-components';
import Data from './CommonSectionData';

const CommonRightSection = () => {
    return (
        <>
            <CommonRightSectionContainer>
                <div className="container">
                    <div className="row">
                        {
                            Data.map(({ id, imgsrc, videosrc, col }) => {
                                <div className={col}>
                                    <div className="img" key={id}>
                                        <img src={imgsrc} />
                                        <div className="video">
                                            <video autoPlay loop muted>
                                                <source src={videosrc}
                                                    type="video/mp4" />
                                            </video>
                                        </div>
                                    </div>
                                </div>
                            })
                        }
                    </div>
                </div>
            </CommonRightSectionContainer>
        </>
    )
}

const CommonRightSectionContainer = styled.div`
        background:black;
        border-top:10px solid gray;
        color:#fff;
        .col-lg-6{
            padding:40px 45px;
        }
        .img{
            position:relative;
            overflow:hidden;

            img{
                position:relative;
                z-index:2;
                max-width:100%;
                height:auto;
            }
            .video{
                width: 100%;
                height: 100%;
                max-width: 73%;
                max-height: 54%;
                position: absolute;
                top: 46%;
                left: 50%;
                transform: translate(-50%,-50%);
                video{
                    width:100%;
                    height:100%;
                }
            }
        }
        @media(min-width:200px) and (max-width:600px){
            padding: 50px 5px;
            text-align:center;
            .img{
                margin-top:1rem;
            }
        }
        @media(min-width:601px) and (max-width:992px){
            padding: 50px 5px;
            text-align:center;
            .img{
                margin-top:0rem;
            }
        }
`

export default CommonRightSection;
