import React from 'react';
import styled from 'styled-components';
import CommonSection from '../Common/CommonSection';

const Section = () => {
    return (
        <>
            <SectionContainer>
                <div className="container-fluid">
                    <div className="row row-sections">
                        
                        <div className="col-lg-6 order-lg-2 order-1 commonheader">
                            <CommonSection heading="Download your shows to watch offline."
                                context="Save your favourites easily and always have something to watch." />
                        </div>

                        <div className="col-lg-6 order-lg-1 order-2">
                            <div className="img">
                                <img src="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/mobile-0819.jpg" />
                                <div className="video">
                                    <video autoPlay loop muted>
                                        <source src=""
                                            type="video/mp4" />
                                    </video>
                                    <div className="downloder">
                                        
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </SectionContainer>
        </>
    )
}

const SectionContainer = styled.div`
        border-top:8px solid #222;
        .img{
            position:relative;
            overflow:hidden;

            img{
                position:relative;
                z-index:2;
                max-width:100%;
                height:auto;
            }
            .video{
                width: 100%;
                height: 100%;
                max-width: 73%;
                max-height: 54%;
                position: absolute;
                top: 46%;
                left: 50%;
                transform: translate(-50%,-50%);
                video{
                    width:100%;
                    height:100%;
                }
                .downloder{
                    border:1px solid black;
                    height:10px;
                    width:50px;
                }
            }
        }
        
        @media(min-width:200px) and (max-width:600px){
            text-align:center;
            .img{
                margin-top:1rem;
            }
        }
        @media(min-width:601px) and (max-width:992px){
            text-align:center;
            .img{
                margin-top:0rem;
            }
        }
`

export default Section;
