import React, { useState } from 'react';
import styled from 'styled-components';
import CommonEmailInput from '../Common/CommonEmailInput';

const Faq = () => {

    const [seePara, setSeePara] = useState(false)

    function paraopen(){
        let p = document.getElementById("p");
        p.classList.toggle("pactive")
        let times = document.getElementById("times");
        times.classList.toggle("fa-active")
    }

    return (
        <>
            <FaqContainer>
                <div className="container-fluid p-5">
                    <div className="row">
                        <h1 className="text-center">Frequently Asked Questions</h1>
                        <div className="acc">
                            <div className="acc-body text-center d-flex" onClick={paraopen}>
                                <div className="acc-title">
                                    <h3>What is Netfliex</h3><i className="fas fa-plus"></i>
                                    <i className="fas fa-times"></i>
                                </div>
                            </div>
                            <div className="p" id="p">
                                <p>
                                    Netflix is a streaming service that offers a wide variety of award-winning TV
                                    shows, movies, anime, documentaries and more – on thousands of internet-connected
                                    devices. You can watch as much as you want, whenever you want, without a single
                                    ad – all for one low monthly price. There's always something new to discover,
                                    and new TV shows and movies are added every week!
                                </p>
                            </div>
                        </div>

                        <div className="email">
                            <CommonEmailInput />
                        </div>
                    </div>
                </div>
            </FaqContainer>
        </>
    )
}

const FaqContainer = styled.div`
            border-top:10px solid gray;
            .acc{
                    border-bottom:1px solid black;
                margin:1px auto;
                background:red;
            width:50%;
            margin:10px auto;
            cursor:pointer;
            .acc-body{
                .acc-title{
                    display:flex;
            width:100%;
            justify-content:space-between;
            align-items:center;
                }
            margin: 2px auto;

    }
        .p, .fa-times{
            display:none;
        }
        .pactive, .fa-active{
            display:flex;
        }
    }
            `

export default Faq
