import React from 'react';
import styled from 'styled-components';

const Navbar = () => {
    return (
        <>
            <NavbarContainer>
                <div className="navbar">
                    <div className="logo">
                        <span>Netflix</span>
                    </div>
                    <div className="rightside">
                        <div className="language">
                            <span><i class="fas fa-globe"></i></span>
                            <select>
                                <option>English</option>
                                <option>Hindi</option>
                            </select>
                        </div>
                        <span>Sign Up</span>
                    </div>
                </div>
            </NavbarContainer>
        </>
    )
}

const NavbarContainer = styled.div`
    position:relative;
    color:white;
    padding: 0px 4rem;
    ${'' /* line-height: 70px; */}
    .logo{
        span{
            color:red;
            font-size: 2rem;
            font-weight: 600;
            text-transform:uppercase;
        }
    }
    .rightside{
        display:flex;
        align-items:center;
        .language{
            border:1px solid white;
            margin-right:2rem;
            padding:0.300rem 0.500rem;
        select{
            border:none;
            background:transparent;
            color:white;
            option{
                background:gray;
            }
        }
        }
    }
`

export default Navbar;
