import React from 'react';
import styled from 'styled-components';
import Data from './CommonSectionData';

const CommonSection = () => {
    return (
        <>
            <CommonContainer>
                <div className="container-fluid">
                    <div className="row">
                        {
                            Data.map(({ id, heading, context, imgsrc, videosrc, col,order }) => {
                                return (
                                    <>

                                           <div className={order}>
                                        <div className={col} style={{ display: "flex", justifyContent: "center", flexDirection: 'column' }} key={id}>
                                            <h1>{heading}</h1>
                                            <h2>{context}</h2>
                                        </div>

                                        <div className={col}>
                                            <div className="img">
                                                <img src={imgsrc} />
                                                <div className="video">
                                                    <video autoPlay loop muted>
                                                        <source src={videosrc}
                                                            type="video/mp4" />
                                                    </video>
                                                </div>
                                            </div>
                                           </div>
                                        </div>

                                    </>
                                )
                            })
                        }
                    </div>
                </div>
            </CommonContainer>
        </>
    )
}

const CommonContainer = styled.div`
        background:black;
        ${'' /* padding:40px 45px; */}
        border-top:10px solid gray;
        color:#fff;
        padding: 0px 40px;
        .col-lg-6{
            padding:40px 45px;
        }
        .header{
        display:flex;
        flex-direction:column;
        justify-content:center;
        h1{
            font-size:3.125rem;
            line-height:1.1;
            margin-bottom: 1.5rem;
        }  
        h2{
            font-size:1.625rem;
            font-weight:400;
        } 
        }
        .img{
            position:relative;
            overflow:hidden;

            img{
                position:relative;
                z-index:2;
                max-width:100%;
                height:auto;
            }
            .video{
                width: 100%;
                height: 100%;
                max-width: 73%;
                max-height: 54%;
                position: absolute;
                top: 46%;
                left: 50%;
                transform: translate(-50%,-50%);
                video{
                    width:100%;
                    height:100%;
                }
            }
        }
        
        @media(min-width:200px) and (max-width:600px){
            padding: 50px 5px;
            text-align:center;
            .col-lg-6{
                padding: 20px 10px;
            }
            h1{
                font-size: 1.625rem !important;
            }
            h2{
                font-size: 1.125rem !important;
            }
            .img{
                margin-top:1rem;
            }
        }
        @media(min-width:601px) and (max-width:992px){
            padding: 50px 5px !important;
            text-align:center;
            h1{
                font-size: 2.5rem !important;
            }
            h2{
                font-size: 1.25rem !important;
            }
            .img{
                margin-top:0rem;
            }
        }
`

export default CommonSection;
