import React, { useState } from 'react';
import styled from 'styled-components';
import CommonEmailInput from '../Common/CommonEmailInput';

const Faq = () => {

    const [clicked,setClicked] = useState(false);

    const toggle = index =>{
        if(clicked === index){
            return setClicked(null);
        }
        setClicked(index);
    }   

    return (
        <>
            <FaqContainer>
                <div className="container-fluid p-5">
                    <div className="row">
                        <h1 className="text-center">Frequently Asked Questions</h1>

                        <div className="accordion">
                            <div className="acc" onClick={()=> toggle(i)}>
                                <h1>What Is Netflix?</h1>
                                <span>{clicked === i ? '<i className="fas fa-times"></i>' : '<i className="fas fa-plus"></i>'}</span>
                            </div>
                            <div className="acc-ans">
                                <p>This is Netflix</p>
                            </div>
                        </div>

                        <div className="email">
                            <CommonEmailInput />
                        </div>
                    </div>
                </div>
            </FaqContainer>
        </>
    )
}

const FaqContainer = styled.div`
    border-top:10px solid gray;
    .accordion{
        background:gray;
        width:60%;
        margin:0px auto;
        .acc{
            display:flex;
            align-items:center;
            justify-content:space-between;
        }
    }
`

export default Faq
