import React, { useState } from 'react';
import styled from 'styled-components';
import CommonEmailInput from '../Common/CommonEmailInput';

const Faq = () => {


    return (
        <>
            <FaqContainer>
                <div className="container-fluid p-5">
                    <div className="row">
                        <h1 className="text-center">Frequently Asked Questions</h1>

                        <div className="accordion" id="accordionPanelsStayOpenExample">
                            <div className="accordion-item">
                                <h2 className="accordion-header" id="panelsStayOpen-headingOne">
                                    <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#panelsStayOpen-collapseOne" aria-expanded="true" aria-controls="panelsStayOpen-collapseOne">
                                        What is Netflix ?
                                    </button>
                                </h2>
                                <div id="panelsStayOpen-collapseOne" className="accordion-collapse collapse show" aria-labelledby="panelsStayOpen-headingOne">
                                    <div className="accordion-body">
                                        <p>
                                        Netflix is a streaming service that offers a wide variety of award-winning 
                                        TV shows, movies, anime, documentaries and more – on thousands of internet-connected 
                                        devices. You can watch as much as you want, whenever you want, without a 
                                        single ad – all for one low monthly price. There's always something new to 
                                        discover, and new TV shows and movies are added every week!
                                        </p>
                                    </div>
                                </div>
                            </div> 
                        </div>

                        <div classNameName="email">
                            <CommonEmailInput />
                        </div>
                    </div>
                </div>
            </FaqContainer>
        </>
    )
}

const FaqContainer = styled.div`
        border-top:10px solid gray;
        .accordion{
            width:65%;
            margin:0px auto;
            p{
                color:black;
            }
        }
    }
`

export default Faq
