import React from 'react';
import styled from 'styled-components';

const CommonSection = () => {
    return (
        <>
            <CommonContainer>
                <div className="container">
                    <div className="row">
                        <div className="col-lg-5 header">
                            <h1>Enjoy on your TV.</h1>
                            <p>Watch on smart TVs, PlayStation, Xbox, Chromecast, Apple TV,
                                Blu-ray players and more.</p>
                        </div>
                        <div className="col-lg-7">
                            <div className="img">
                                <img src='/images/tv.png' />
                                <div className="video">
                                    <video autoPlay loop muted>
                                        <source src="https://assets.nflxext.com/ffe/siteui/acquisition/ourStory/fuji/desktop/video-tv-in-0819.m4v"
                                            type="video/mp4" />
                                    </video>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </CommonContainer>
        </>
    )
}

const CommonContainer = styled.div`
        background:black;
        padding:40px 45px;
        border-top:10px solid gray;
        .header{
        display:flex;
        flex-direction:column;
        justify-content:center;
        h1,p{
            text-align:center;
            color:#fff;
        }   
        }
        .img{
            position:relative;
            overflow:hidden;
            img{
                position:relative;
                z-index:2;
                width:100%;
                height:auto;
            }
            .video{
                position:absolute;
                top:46%;
                left:50%;
                transform: translate(-50%,-50%);
            }
        }
        @media(max-width:992px){
            padding: 50px 5px;
        }
`

export default CommonSection;
