import React from 'react';
import styled from 'styled-components';
import Data from './CommonSectionData';

const CommonLeftSection = () => {
    return (
        <>
            <CommonLeftSectionContainer>
                <div className="container">
                    <div className="row">
                        {
                            Data.map(({ id, heading, context,col }) => {
                                <div className={col} style={{ display: "flex", justifyContent: "center", flexDirection: 'column' }} key={id}>
                                    <h1>{heading}</h1>
                                    <h2>{context}</h2>
                                </div>
                            })
                        }
                    </div>
                </div>
            </CommonLeftSectionContainer>
        </>
    )
}

const CommonLeftSectionContainer = styled.div`
        background:black;
        ${'' /* border-top:10px solid gray; */}
        color:#fff;
        .col-lg-6{
            padding:40px 45px;
        }
        h1{
            font-size:3.125rem;
            line-height:1.1;
            margin-bottom: 1.5rem;
        }  
        h2{
            font-size:1.625rem;
            font-weight:400;
        } 
        @media(min-width:200px) and (max-width:600px){
            h1{
                font-size: 1.625rem !important;
            }
            h2{
                font-size: 1.125rem !important;
            }
        }
        @media(min-width:601px) and (max-width:992px){
            h1{
                font-size: 2.5rem !important;
            }
            h2{
                font-size: 1.25rem !important;
            }   
        }
`

export default CommonLeftSection;
