import React from 'react';
import styled from 'styled-components';
import Data from './CommonSectionData';

const CommonSection = () => {
    return (
        <>
            <CommonContainer>
                <div className="container">
                    <div className="row">
                        {
                            Data.map(({ id, heading, context, imgsrc, videosrc, col }) => {
                                return (
                                    <>


                                        
                                    
                                    </>
                    )
                            })
                        }
                </div>
                </div>
        </CommonContainer>
        </>
    )
}

const CommonContainer = styled.div`
        background:black;
        border-top:10px solid gray;
        color:#fff;
        .col-lg-6{
            padding:40px 45px;
        }
        .header{
        display:flex;
        flex-direction:column;
        justify-content:center;
        h1{
            font-size:3.125rem;
            line-height:1.1;
            margin-bottom: 1.5rem;
        }  
        h2{
            font-size:1.625rem;
            font-weight:400;
        } 
        }
        .img{
            position:relative;
            overflow:hidden;

            img{
                position:relative;
                z-index:2;
                max-width:100%;
                height:auto;
            }
            .video{
                width: 100%;
                height: 100%;
                max-width: 73%;
                max-height: 54%;
                position: absolute;
                top: 46%;
                left: 50%;
                transform: translate(-50%,-50%);
                video{
                    width:100%;
                    height:100%;
                }
            }
        }
        
        @media(min-width:200px) and (max-width:600px){
            padding: 50px 5px;
            text-align:center;
            h1{
                font-size: 1.625rem !important;
            }
            h2{
                font-size: 1.125rem !important;
            }
            .img{
                margin-top:1rem;
            }
        }
        @media(min-width:601px) and (max-width:992px){
            padding: 50px 5px;
            text-align:center;
            h1{
                font-size: 2.5rem !important;
            }
            h2{
                font-size: 1.25rem !important;
            }
            .img{
                margin-top:0rem;
            }
        }
`

export default CommonSection;
